import React, { Component } from 'react';

import {Tabs, WhiteSpace} from 'antd-mobile'
import DNAxios from '../NetworkCenter/DNAxios'
import DNApiName from '../NetworkCenter/DNApiName'

const tabs = [
    { title: '首页' },
    { title: 'Android'},
    { title: '前端' },
    { title: 'iOS'},
    { title: '产品' },
    { title: '设计'},
    { title: '后端' },
];

export default class Home extends Component{

    componentDidMount(){
        this.getHotRecommendInfo()
    }



    // 获取首页热门推荐数据
    getHotRecommendInfo(param){
        debugger
        DNAxios.get(DNApiName.ApiForEntryHotRecomment , {}).then(rsp => {
            console.log(rsp)
        }).catch(err => {

        })
    }

    renderContent = tab =>(
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', backgroundColor: '#fff' ,flexDirection:'column'}}>
            <p>Content of {tab.title}</p>
        </div>
    );

    render(){
        return (
            <div>
                {/* 顶部导航栏 */}
                <div style={{ height: "100%" }}>
                    <Tabs tabs={tabs}
                        initalPage={'2'}
                        renderTabBar={props => <Tabs.DefaultTabBar {...props} />}
                    >
                    {this.renderContent}
                    </Tabs>
                </div>



            </div>
        );
    }
}
