import React, { Component } from 'react';

import {Tabs, WhiteSpace} from 'antd-mobile'
import DNAxiosTool from '../NetworkCenter/DNAxios'
import DNApiName from '../NetworkCenter/DNApiName'

import axios from 'axios'

const tabs = [
    { title: '首页' },
    { title: 'Android'},
    { title: '前端' },
    { title: 'iOS'},
    { title: '产品' },
    { title: '设计'},
    { title: '后端' },
];

let baseUrl = "https://timeline-merger-ms.juejin.im";

if(process.env.NODE_ENV === 'development'){
    baseUrl = "http://localhost:3000"
}

var instance = axios.create({
    // baseURL: 'https://timeline-merger-ms.juejin.im',
    baseURL:baseUrl,
    timeout: 10000,
    responseType : 'json',
    headers: {
        'Content-Type': 'application/x-www-form-urlencoded; charset=utf-8',
    }
  });

export default class Home extends Component{

    componentDidMount(){
        this.getHotRecommendInfo()
    }

    // 获取首页热门推荐数据
    getHotRecommendInfo(param){


        var ajax = new XMLHttpRequest();
        ajax.onreadystatechange = function(){
            debugger
            console.log(ajax.readyState);
            console.log(ajax.status);
            // 第五步：在监听函数中，判断readyState=4 && status=200表示请求成功
            if(ajax.readyState==4 && ajax.status==200){
                // 第六步： 使用responseText、responseXML接受响应数据，并使用原生JS操作DOM进行显示
                console.log(ajax.response);
                console.log(ajax.responseXML);// 返回不是XML，显示null
                console.log(JSON.parse(ajax.responseText));
                console.log(eval("("+ajax.responseText+")"));
            }
        }
        // 第三步： open一个链接
        ajax.open("GET",baseUrl + '/v1/get_entry_by_hot_recomment?device_id=D2F3A47D-6BCA-446A-8D91-C2B7F1068DE1&limit=3&src=ios&token=eyJhY2Nlc3NfdG9rZW4iOiJWMGZIQjNYd1ROazZNRXEzIiwicmVmcmVzaF90b2tlbiI6IllOT3hBMXhOSVU4dXNiSmEiLCJ0b2tlbl90eXBlIjoibWFjIiwiZXhwaXJlX2luIjoyNTkyMDAwfQ%3D%3D&uid=5b8390f3e51d45389d3b5028',true);//true异步请求，false同步



        DNAxiosTool.get(DNApiName.ApiForEntryHotRecomment , {
            "device_id" : 'D2F3A47D-6BCA-446A-8D91-C2B7F1068DE1',
            "limit" : '3',
            "src" : 'ios',
            "token" : "eyJhY2Nlc3NfdG9rZW4iOiJWMGZIQjNYd1ROazZNRXEzIiwicmVmcmVzaF90b2tlbiI6IllOT3hBMXhOSVU4dXNiSmEiLCJ0b2tlbl90eXBlIjoibWFjIiwiZXhwaXJlX2luIjoyNTkyMDAwfQ%3D%3D",
            "uid" : "5b8390f3e51d45389d3b5028"
        }).then(rsp => {
            console.log(rsp)
        }).catch(err => {
        })
    }

    renderContent = tab =>(
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', height: '100%', backgroundColor: '#fff' ,flexDirection:'column'}}>
            <p>Content of {tab.title}</p>
        </div>
    );

    render(){
        return (
            <div>
                {/* 顶部导航栏 */}
                <div style={{ height: "100%" }}>
                    <Tabs tabs={tabs}
                        initalPage={'2'}
                        renderTabBar={props => <Tabs.DefaultTabBar {...props} />}
                    >
                    {this.renderContent}
                    </Tabs>
                </div>



            </div>
        );
    }
}
